import React from 'react';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import InfoRoom from './InfoRoom/InfoRoom';
import ContentChat from './ContentChat/ContentChat';
import queryString from "query-string"
import { useHistory } from 'react-router-dom';
import io from "socket.io-client"

const useStyles = makeStyles(() => ({
    pageChat: {
        height: "100vh"
    },
    img: {
        width: 100,
        height: 100,
        objectFit: "cover"
    }
  }));

const socket = io("http://localhost:8000")

function PageChat({ location }) {
    const [name, setName] = React.useState("");
    const [room, setRoom] = React.useState("");
    const classes = useStyles()
    const history = useHistory();


    React.useEffect(() => {
        const data = queryString.parse(location.search);
        if ( location.search ) {
            setName(data.name);
            setRoom(data.room);
        } else {
            history.push("/")
        }
        return () => {
        }
    }, [location.search, history, name, room])

    return (
        <Grid container  className={classes.pageChat}>
            <InfoRoom name={name} room={room}/>
            <ContentChat />
            <img src="https://todan365.com/wp-content/uploads/2021/06/JAV-Idol-top-803.jpg" alt="alfgdhgihgih"  className={classes.img} />
        </Grid>
    );
}

export default PageChat;
