    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.secondary,
      height: "100vh"
    },
    avartar: {
      display: "flex",
      alignItems: "center"
    },
    mrleft: {
      margin: "0 20px",
      width: "100%",
      textAlign: 'left',
      whiteSpace: "nowrap",
      overflow: "hidden",
      textOverflow: "ellipsis",
    }
